import React from 'react';
import './PolicyDetail.css'; // 필요한 스타일 정의

function PolicyDetail() {
    return (
        <div className="policy-detail-container">
            <header className="policy-header">
                <h1>정책아이디어</h1>
            </header>
            <div className="policy-content">
                <h2>대학생 전공책 바우처</h2>
                <p>대상: 대학생</p>
                <p>#대학생 #바우처 #학업지원</p>
                <div className="policy-background">
                    <h3>배경 및 필요성</h3>
                    <p>대학생들은 학업을 위해 많은 비용을 지출해야 하며...</p>
                </div>
                <div className="policy-goal">
                    <h3>정책 목표</h3>
                    <p>이 정책의 목표는 대학생들의 경제적 부담...</p>
                </div>
                {/* 별점, 평가, 관련 아이디어 섹션 */}
            </div>
        </div>
    );
}

export default PolicyDetail;
