import React from 'react';
import { useNavigate } from 'react-router-dom';
import './YouthRecommendation.css';
import PolicyData_Temp from './PolicyData_Temp'; // Ï†ïÏ±Ö Îç∞Ïù¥ÌÑ∞ ÏûÑÌè¨Ìä∏
import FilterButtons from './FilterButtons';

function YouthRecommendation() {
    const navigate = useNavigate();

    // ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
    const goToPolicyDetail = (policyId) => {
        navigate(`/PolicyDetail/${policyId}`);
    };

    return (
        <div className="recommendation-container">
            <header className="recommendation-header">
                <span className="time">9:41</span>
                <span className="title">Recommendation</span>
                <span className="icons">
                    <span role="img" aria-label="battery">üîã</span>
                </span>
            </header>

            <div className="search-bar">
                <input type="text" placeholder="Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..." disabled />
            </div>

            <div className="greeting">
                <p>23ÏÑ∏ ÎåÄÌïôÏÉùÏù¥Ïã† Ï≤≠ÎÖÑÎãò ÏïàÎÖïÌïòÏÑ∏Ïöî‚ù§Ô∏è</p>
            </div>
            <div className="greeting2">
                <p>Ìï¥Îãπ Ï†ïÎ≥¥Ïóê ÎßûÏ∂∞ Ï∂îÏ≤úÎêòÍ≥† ÏûàÏñ¥Ïöî.</p>
            </div>

            <div className="button-group">
                <FilterButtons /> {/* Î≤ÑÌäº Í∑∏Î£π Ïª¥Ìè¨ÎÑåÌä∏ */}
            </div>

            {/* Ï†ïÏ±Ö Ïπ¥Îìú Ïª®ÌÖåÏù¥ÎÑà */}
            <div className="card-container">
                {PolicyData_Temp.map((policy) => (
                    <div
                        className="card"
                        key={policy.id} // Í≥†Ïú† ÌÇ§ ÏÑ§Ï†ï
                        onClick={() => goToPolicyDetail(policy.id)} // Ï†ïÏ±Ö ID Ï†ÑÎã¨
                    >
                        <div className="card-header">
                            <span>{policy.region || 'ÏßÄÏó≠ Ï†ïÎ≥¥ ÏóÜÏùå'}</span>
                            <span>{policy.title}</span>
                        </div>
                        <img
                            src={policy.image || '/images/placeholder.png'} // Ïù¥ÎØ∏ÏßÄ Í≤ΩÎ°ú ÏÑ§Ï†ï
                            alt={policy.title}
                            className="card-image"
                        />
                        <div className="card-body">
                            <p>Î™®ÏßëÎßàÍ∞êÏùº: {policy.deadline || 'Ï†ïÎ≥¥ ÏóÜÏùå'}</p>
                            <p>{policy.description}</p>
                        </div>
                        <div className="card-footer">
                            <span>‚≠ê {policy.rating || 'N/A'} ({policy.reviews || 0})</span>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default YouthRecommendation;
