import React, { useState } from 'react';
import PolicyData_Temp from './PolicyData_Temp';

const CustomPolicySearch = () => {
    const [selectedAge, setSelectedAge] = useState(20); // 기본값: 20세
    const [selectedCategory, setSelectedCategory] = useState('주거지원형'); // 기본값: 주거지원형

    // 필터링 함수
    const filteredPolicies = PolicyData_Temp.filter(
        (policy) =>
            policy.age >= selectedAge &&
            policy.age <= 25 && // 나이 조건 (20~25세)
            policy.category === selectedCategory // 선택된 카테고리 조건
    );

    return (
        <div className="custom-policy-container">
            <header className="custom-policy-header">
                <h1>맞춤 정책찾기</h1>
            </header>

            {/* 나이 선택 */}
            <div className="input-group">
                <label htmlFor="age-select">나이를 알려주세요</label>
                <select
                    id="age-select"
                    value={selectedAge}
                    onChange={(e) => setSelectedAge(parseInt(e.target.value))}
                >
                    {[20, 21, 22, 23, 24, 25].map((age) => (
                        <option key={age} value={age}>
                            {age}세
                        </option>
                    ))}
                </select>
            </div>

            {/* 카테고리 선택 */}
            <div className="input-group">
                <label htmlFor="category-select">정책 카테고리를 설정해주세요</label>
                <select
                    id="category-select"
                    value={selectedCategory}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                >
                    {['주거지원형', '창업지원형', '기본소득형'].map((category) => (
                        <option key={category} value={category}>
                            {category}
                        </option>
                    ))}
                </select>
            </div>

            {/* 필터링된 정책 리스트 */}
            <div className="policy-list">
                {filteredPolicies.length > 0 ? (
                    filteredPolicies.map((policy) => (
                        <div key={policy.id} className="policy-card">
                            <h3>{policy.title}</h3>
                            <p>{policy.description}</p>
                            <img src={policy.image} alt={policy.title} />
                        </div>
                    ))
                ) : (
                    <p>조건에 맞는 정책이 없습니다.</p>
                )}
            </div>
        </div>
    );
};

export default CustomPolicySearch;
