import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import ProposedPolicyData from './PolicyData_proposaled'; // Import proposed policies
import './PolicyDetail_Proposal.css';

const PolicyDetailProposal = ({ match }) => {
    const { id } = useParams(); //  정책 ID 가져오기
    //const { id } = match.params; // Get policy ID from route parameters
    const policy = ProposedPolicyData.find((item) => item.id === id);

    const [comments, setComments] = useState(policy.comments || []);
    const [newComment, setNewComment] = useState('');
    const [rating, setRating] = useState(policy.rating || 0);
    const [likes, setLikes] = useState(policy.likes || 0);

    if (!policy) {
        return <div>해당 정책 정보를 찾을 수 없습니다.</div>;
    }

    // Add a new comment
    const handleAddComment = () => {
        if (newComment.trim()) {
            setComments([...comments, { text: newComment, likes: 0 }]);
            setNewComment('');
        }
    };

    // Delete a comment
    const handleDeleteComment = (index) => {
        const updatedComments = comments.filter((_, idx) => idx !== index);
        setComments(updatedComments);
    };

    // Like a comment
    const handleLikeComment = (index) => {
        const updatedComments = comments.map((comment, idx) =>
            idx === index ? { ...comment, likes: comment.likes + 1 } : comment
        );
        setComments(updatedComments);
    };

    // Rate the policy
    const handleRate = (newRating) => {
        setRating(newRating);
    };

    return (
        <div className="policy-detail-container">
            <header className="policy-header">
                <span className="back-button" onClick={() => window.history.back()}>〈 정책목록</span>
                <h1>{policy.title}</h1>
            </header>
            <img src={policy.image} alt={policy.title} className="policy-image" />
            <div className="policy-info">
                <p className="policy-tags">{policy.tags.map(tag => `#${tag}`).join(' ')}</p>
                <p className="policy-background"><strong>배경 및 필요성:</strong> {policy.background}</p>
                <p className="policy-purpose"><strong>정책 목표:</strong> {policy.purpose}</p>
            </div>
            <div className="policy-rating">
                <p>⭐ {rating} ({likes} likes)</p>
                {[1, 2, 3, 4, 5].map((star) => (
                    <span
                        key={star}
                        className={`star ${star <= rating ? 'active' : ''}`}
                        onClick={() => handleRate(star)}
                    >
                        ★
                    </span>
                ))}
            </div>
            <div className="policy-comments">
                <h3>댓글</h3>
                <div className="comment-input">
                    <input
                        type="text"
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        placeholder="댓글을 입력하세요..."
                    />
                    <button onClick={handleAddComment}>추가</button>
                </div>
                <ul>
                    {comments.map((comment, index) => (
                        <li key={index}>
                            <p>{comment.text}</p>
                            <div>
                                <span onClick={() => handleLikeComment(index)}>❤️ {comment.likes}</span>
                                <span onClick={() => handleDeleteComment(index)}>삭제</span>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
            <div className="related-policies">
                <h3>관련 정책</h3>
                <div className="related-container">
                    {ProposedPolicyData.slice(0, 2).map((relatedPolicy) => (
                        <div key={relatedPolicy.id} className="related-card">
                            <img src={relatedPolicy.image} alt={relatedPolicy.title} />
                            <p>{relatedPolicy.title}</p>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default PolicyDetailProposal;
