import React, { use, useState, useEffect, useCallback } from 'react';
import './PolicyList.css';

// 예시 정책 데이터
const examplePolicies = [
    {
        id: 1,
        title: '청년 창업 지원 프로젝트',
        description: '창업을 꿈꾸는 청년들을 위한 맞춤형 지원 프로그램입니다.',
        image: '/images/placeholder1.png',
    },
    {
        id: 2,
        title: '주거 안정 지원',
        description: '청년 임대주택과 관련된 주거 안정 정책입니다.',
        image: '/images/placeholder2.png',
    },
    {
        id: 3,
        title: 'IT 기술 교육 지원',
        description: '청년들을 위한 최신 IT 기술 교육을 제공합니다.',
        image: '/images/placeholder3.png',
    },
    {
        id: 4,
        title: '사회적 기업 지원',
        description: '사회적 가치를 실현하는 기업을 위한 지원 정책입니다.',
        image: '/images/placeholder4.png',
    },
    {
        id: 5,
        title: '대학 학자금 대출 지원',
        description: '청년들의 학자금 대출 부담을 줄이기 위한 정책입니다.',
        image: '/images/placeholder5.png',
    },
    {
        id: 6,
        title: '문화 예술 창작 지원',
        description: '문화 예술 활동을 지원하는 프로그램입니다.',
        image: '/images/placeholder6.png',
    },
    {
        id: 7,
        title: '해외 유학 장학금 지원',
        description: '해외 유학을 꿈꾸는 청년들에게 장학금을 지원합니다.',
        image: '/images/placeholder7.png',
    },
    {
        id: 8,
        title: '농촌 청년 창업 지원',
        description: '농촌 지역에서 창업을 희망하는 청년들을 위한 지원 정책입니다.',
        image: '/images/placeholder8.png',
    },
    {
        id: 9,
        title: '스포츠 활동 지원',
        description: '건강한 청년들을 위한 스포츠 관련 정책입니다.',
        image: '/images/placeholder9.png',
    },
    {
        id: 10,
        title: '코딩 부트캠프 지원',
        description: '코딩 기술을 배우고 싶은 청년들을 위한 부트캠프 프로그램입니다.',
        image: '/images/placeholder10.png',
    },
    {
        id: 11,
        title: '청년 금융 교육',
        description: '재무 관리를 배우고 싶은 청년들을 위한 금융 교육 프로그램입니다.',
        image: '/images/placeholder11.png',
    },
    {
        id: 12,
        title: '글로벌 교환 학생 프로그램',
        description: '글로벌 교환 학생으로 해외 경험을 쌓을 기회를 제공합니다.',
        image: '/images/placeholder12.png',
    },
];

const PolicyList = () => {
    const [policies, setPolicies] = useState([]); // 정책 리스트
    const [page, setPage] = useState(1); // 현재 페이지
    const [loading, setLoading] = useState(false); // 로딩 상태

    // 정책 데이터 가져오기
    const fetchPolicies = useCallback(() => {
        setLoading(true);
        setTimeout(() => {
            const startIndex = (page - 1) * 10;
            const newPolicies = examplePolicies.slice(startIndex, startIndex + 10); // 10개씩 가져오기
            setPolicies((prev) => [...prev, ...newPolicies]);
            setLoading(false);
        }, 1000); // 로딩 시뮬레이션 (1초 대기)
    }, [page]);

    const PolicyList = () => {
        const navigate = useNavigate();

        const handleCardClick = (id) => {
            navigate(`/policy/${id}`);
        };
    }
    // 스크롤 이벤트 핸들러
    const handleScroll = useCallback(() => {
        if (
            window.innerHeight + document.documentElement.scrollTop >=
            document.documentElement.offsetHeight - 10
        ) {
            setPage((prevPage) => prevPage + 1);
        }
    }, []);

    useEffect(() => {
        fetchPolicies(); // 초기 데이터 로드 및 페이지 변경 시 추가 로드
    }, [fetchPolicies]);

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll); // 클린업
    }, [handleScroll]);

    return (
        <div className="policy-list-container">
            <div className="policy-card-container">
                {policies.map((policy) => (
                    <div className="policy-card" key={policy.id}>
                        <div className="policy-card-header">{policy.title}</div>
                        <img
                            src={policy.image}
                            alt={policy.title}
                            className="policy-card-image"
                        />
                        <div className="policy-card-body">{policy.description}</div>
                    </div>
                ))}
            </div>
            {loading && (
                <div className="loading-spinner">
                    <div className="spinner"></div>
                    <p>Loading...</p>
                </div>
            )}
        </div>
    );
};

export default PolicyList;
