import React, { useState, useEffect, useCallback } from 'react';
import './PolicyList.css';

const PolicyList = () => {
    const [policies, setPolicies] = useState([]); // 정책 리스트
    const [page, setPage] = useState(1); // 현재 페이지
    const [loading, setLoading] = useState(false); // 로딩 상태

    // 서버에서 데이터 가져오기 (예시 API 호출)
    const fetchPolicies = useCallback(async () => {
        setLoading(true);
        try {
            const response = await fetch(`https://example.com/api/policies?page=${page}&limit=10`);
            const newPolicies = await response.json();
            setPolicies((prev) => [...prev, ...newPolicies]); // 기존 리스트에 추가
        } catch (error) {
            console.error('Failed to fetch policies:', error);
        } finally {
            setLoading(false);
        }
    }, [page]);

    // 스크롤 이벤트 핸들러
    const handleScroll = useCallback(() => {
        if (
            window.innerHeight + document.documentElement.scrollTop >=
            document.documentElement.offsetHeight - 10
        ) {
            setPage((prevPage) => prevPage + 1);
        }
    }, []);

    useEffect(() => {
        fetchPolicies(); // 초기 데이터 로드 및 페이지 변경 시 추가 로드
    }, [fetchPolicies]);

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll); // 클린업
    }, [handleScroll]);

    return (
        <div className="policy-list-container">
            <div className="policy-card-container">
                {policies.map((policy, index) => (
                    <div className="policy-card" key={index}>
                        <div className="policy-card-header">{policy.title}</div>
                        <img
                            src={policy.image || '/images/placeholder.png'}
                            alt={policy.title}
                            className="policy-card-image"
                        />
                        <div className="policy-card-body">{policy.description}</div>
                    </div>
                ))}
            </div>
            {loading && (
                <div className="loading-spinner">
                    <div className="spinner"></div>
                    <p>Loading...</p>
                </div>
            )}
        </div>
    );
};

export default PolicyList;
